targets.expr:
avg ((
  (
    node_memory_MemTotal{instance=~".*"} or
    node_memory_MemTotal_bytes{instance=~".*"}
  ) - ((
    node_memory_MemFree{instance=~".*"} or
    node_memory_MemFree_bytes{instance=~".*"}) +
    (
      node_memory_Cached{instance=~".*"} or
      node_memory_Cached_bytes{instance=~".*"}
    ) + (
      node_memory_Buffers{instance=~".*"} or
      node_memory_Buffers_bytes{instance=~".*"}
    ) + (
      node_memory_Slab{instance=~".*"} or
      node_memory_Slab_bytes{instance=~".*"}
    )
  )
) / (
  node_memory_MemTotal{instance=~".*"} or
   node_memory_MemTotal_bytes{instance=~".*"}
))
